using Concordium.Sdk.Types.New;

namespace Tests.TestUtilities.Builders;

public class IdentityProviderInfoBuilder
{
    private uint _ipIdentity = 1;
    private string _ipCdiVerifyKey = "d765079462927b68dde779436f0c685eeff250d6075f5acafd6fb0b54183d25c";
    private string _ipVerifyKey = "";
    private string _ipName = "foo bar";
    private string _ipUrl = "http://example.com";
    private string _ipDescription = "Lorem ipsum...";

    public IdentityProviderInfo Build()
    {
        var arOrIpDescription = new ArOrIpDescription(_ipName, _ipUrl, _ipDescription);
        return new IdentityProviderInfo(_ipIdentity, arOrIpDescription, _ipVerifyKey, _ipCdiVerifyKey);
    }

    public IdentityProviderInfoBuilder WithIpIdentity(uint value)
    {
        _ipIdentity = value;
        return this;
    }

    public IdentityProviderInfoBuilder WithName(string value)
    {
        _ipName = value;
        return this;
    }

    public IdentityProviderInfoBuilder WithUrl(string value)
    {
        _ipUrl = value;
        return this;
    }

    public IdentityProviderInfoBuilder WithDescription(string value)
    {
        _ipDescription = value;
        return this;
    }
}